import React,{useState}from'react'

import { storage } from "./firebasestorage";

 const FormularioRecetas=(props)=>{
    
    const initialStateValues = {
        camponombre: '',
        campodescripcion: '' , 
        campocomplejidad: '',
        campoingredientes: ''};
      ///la imagen del estado inicial null que cambiara con el evento setImage 
        const [image, setImage] = useState(null);
        const [url, setUrl] = useState("");
    
    const [values, setValues] = useState(initialStateValues);
    //controlo los cambios evitando que la pagina se recarge e informo de los valores de los campos de texto
      const handleSubmit = e =>{
      e.preventDefault();
      console.log(values)
      props.agregarReceta(values);
     };
    //veo cada vez que un campo de ingreso de texto cambie
    //name es el input
    //value es el valor del input
    const handleInputChange= (e) =>{
     const{name,value}=e.target;
      setValues({...values,[name]:value})
      
     };
    //metodos de subir imagen
     
     

   
     

     const cambioEnImagenes=e=>{
        if(e.target.files[0]){setImage(e.target.files[0])}};
     //almacenare en la carpeta fotosRecetas la imagen de entrada
     const subirImagen =()=>{  
       const subirTarea=storage.ref(`fotosRecetas/${image.name}`).put(image);
       subirTarea.on(
        "imagen subida con exito",
        snapshot => {
          const progress = Math.round(
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100
          );
          setProgress(progress);
        },
        error => {
          console.log(error);
        },
        () => {
          storage
            .ref("images")
            .child(image.name)
            .getDownloadURL()
            .then(url => {
              setUrl(url);
            });
        }
      );


     };

     //la parte grafica
return(
<form onSubmit={handleSubmit}  className='card card-body'>
    <h1>Registro de recetas</h1>
    <div className="form-group input-group">
         <input type="text" 
         className='form-control' 
         placeholder='introduzca el nombre de una receta'
         name='camponombre'
         onChange={handleInputChange}
         />
    </div>

    <div> 
        <br/>
        <input type="file" onChange={cambioEnImagenes}/>
        <button onClick={subirImagen}>Subir imagen</button>
        <br/>
           </div>

    <div className="form-group">
         <br/>
         <textarea name="campodescripcion"  
         cols="30" 
         rows="10" 
         className="form-control"
         onChange={handleInputChange}
         >
         </textarea>
    </div>
    <div className="form-group input-group">
         <input type="text" 
         className='form-control' 
         placeholder='complejidad'
         name='campocomplejidad'
         onChange={handleInputChange}
         />
    </div>
    <div>
        <button className="btn btn-primary btn-block"
        >registrar receta</button>
    </div>
</form>
)

};
export default FormularioRecetas;
